import React, { useEffect, useState } from 'react';

function DogBreed(){

    // Dropdown Menu
    const [dogBreed, setDogBreed] = useState([]);
    const fetctImage = async () => {
        const dogUrl = await fetch("https://dog.ceo/api/breeds/list/all");
        const dogList = await dogUrl.json();
        const dogNames = Object.keys(dogList.message);
        setDogBreed(dogNames);
    };
    useEffect(()=> {
        fetctImage();
    })

    // Select Dropdown option
    const [dogImage, setDogImage] = useState([]);
    const SelectBreed = async (event) => {
        const BreedValues = event.target.value
        const dogImageUrl = await fetch(`https://dog.ceo/api/breed/${BreedValues}/images/random`);
        const getDog = await dogImageUrl.json();
        setDogImage(getDog.message);
    };

    useEffect(() => {
        const options = document.getElementById("dropdownmenu");
        if(options != null){
            options.addEventListener("change", (event) => SelectBreed(event));
        }else{
            options.removeEventListener("change", (event) => SelectBreed(event));
        }       
    },[]);

    
       

    return (
      <div>
        <select>
          {dogBreed.map((breed, uniqueId) => (
            <option id="dropdownmenu" key={uniqueId}>
              {breed}
            </option>
          ))}
        </select>

        {dogImage.map((imageUrl) => (
          <img 
            id="dropdownmenu" 
            src={imageUrl}
            key={imageUrl}
            style={{ width: "300px", height: "300px", objectFit: "cover" }}
            alt="dogImages">
            </img>
        ))}
      </div>
    );
    
}



export default DogBreed;
