import { useEffect, useState } from "react";
import NavBar from "../Component/NavBar";
import e from "cors";

function SearchBreed() {
  const [dogs, setDogs] = useState([]);
  const [userInput, setUserInput] = useState("")

  useEffect(() => {
    const fetchBreed = async () => {
      try {
        const res = await fetch(
          "https://api.thedogapi.com/v1/breeds?limit=12", { //limit to 21 images populated
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "x-api-key":
                "live_Xv1NIDcHuYJfH9CnI39Kwmzb0AV6tkxTCQSmsLIaP60jsnCvV9X0T8NZc5Zbl1ak",
            },
          }
        );
        const data = await res.json();
        setDogs(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchBreed();
  }, []);

  function handleUserChange(event) {
    const e = event.target.value
    setUserInput(e);
  }

  const SelectDog = async() => {
    try {
      const res = await fetch(
        "https://api.thedogapi.com/v1/breeds/search", { //limit to 21 images populated
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            "x-api-key":
              "live_Xv1NIDcHuYJfH9CnI39Kwmzb0AV6tkxTCQSmsLIaP60jsnCvV9X0T8NZc5Zbl1ak",
          },
        }
      );
      const data = await res.json();
      setDogs(data);
    } catch (error) {
      console.log(error);
    }
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    SelectDog()
  }
  

  
  function onClickImage() {
    const imageClick = document.getElementById("click-titles");
    imageClick.addEventListener("click",  )
  }

  return (
    <div>
      <NavBar></NavBar>
      <div className="container" id="container">
        <h1 className="search-title">Welcome to the dog World</h1>
        <div className="searchbar-border">
          <form onSubmit="" className="mx-auto">
            <input onChange={handleUserChange}
            value={userInput} 
              name="search"
              id="search"
              type="text"
              placeholder="Search for a dog breed"
              className="py-3 rounded shadow w-3/4 mt-12 indent-6"
            ></input>
          </form>
        </div>

        <div className="grid grid: cols-1 md:grid-cols-2 lg:grid-cols-3 gap-16 my-12 p-8 text-white">
          {dogs.map((dog) => (
            <article
              id="click-tiles"
              className="dog-card-color p-4 rounded px-0 pt-0"
              key={dog.id}>

              <img
                className="rounded md:h-72 w-full object-cover"
                src={dog.image.url}
                key={dog.image.id}
              ></img>
              <h3 className="font-bold mt-6">{dog.name}</h3>
              <p>{dog.bred_for}</p>
            </article>
          ))}
        </div>
      </div>
    </div>
  );
}

export default SearchBreed;
