import React, { useEffect, useState } from 'react';

function DogBreed(){

    // Dropdown Menu
    // Get all of the object for the dogs. For example:
    // {
    //   "message": {
    //      "affenpinscher": [],
    //      "african": [],
    //      "australian": [
    //          "shepherd"
    //      ],
    // }

    // useState with .map() example: 
    // dogBreeds = [" ", " "]
    // setDogBreeds["object", "object"]
    // breed = ["object"(uniqueId), "object"(uniqueId)] in dogBreeds.map(breed)


    //Get all list of dogs for option dropdown menu
    const [dogBreeds, setDogBreeds] = useState([]);
    const fetctDog = async () => {
      const dogUrl = await fetch("https://dog.ceo/api/breeds/list/all");
      const allBreed = await dogUrl.json();
      const dogNames = Object.keys(allBreed.message);
      setDogBreeds(dogNames);
    };
    useEffect(()=> {
        fetctDog();
    }, [])

    // Populate dog once image is selected
    // const [dogImage, setDogImage] = useState();
    // const breedOption = async (event) => {
    // const BreedValues = event.target.value;
    // const dogImageUrl = `https://dog.ceo/api/breed/${BreedValues}/images/random`
    // const dogImage = await fetch(dogImageUrl);
    // const getDog = await dogImage.json();
    // setDogImage(getDog.message);
    // };

    // useEffect(() => {
    //   const options = document.getElementById("dropdownmenu");
    //   if (options != null) {
    //     options.addEventListener("change", (event) => breedOption(event));
    //     return () => {
    //       options.removeEventListener("change", (event) => breedOption(event));
    //     };
    //   }
    // }, []);

     // Get all sub-Breed for option dropdownmenu2
     const [subBreed, setSubBreed] = useState([]);
     const fetchSubBreed = async(event) => {
     const breed = event.target.value;
     const url = `https://dog.ceo/api/breed/${breed}/list`;
     const response = await fetch(url);
     const subBreeds = await response.json();
     const subBreedNames = Object.values(subBreeds)
     setSubBreed(subBreedNames);
     };

     useEffect(()=> {
      fetchSubBreed();
     }, []);
     

  return (
    <div>
      <div className="row">
        <div className="col-6-sm">
          <select id="dropdownmenu">
            <option>Please select a breed</option>
            {dogBreeds.map((breed, uniqueId) => (
              <option key={uniqueId} value={breed}>
                {breed}
              </option>
            ))}
          </select>
        </div>

        <div className="col-6-sm">
          <select id="dropdownmenu2">
            <option>Please select sub breed</option>
            {subBreed.map((breed2, uniqueId2) => {
              <option key={uniqueId2} value={breed2}>
                {breed2}
              </option>;
            })}
          </select>
        </div>

        {/* <div className='col-6-sm'>
          <div className="dogImages">
            {[dogImage].map((specificImage) => (
