import React, { useEffect, useState } from "react";

function DogBreed() {
  // Dropdown Menu
  // Get all of the object for the dogs. For example:
  // {
  //   "message": {
  //      "affenpinscher": [],
  //      "african": [],
  //      "australian": [
  //          "shepherd"
  //      ],
  // }

  // useState with .map() example:
  // dogBreeds = [" ", " "]
  // setDogBreeds["object", "object"]
  // breed = ["object"(uniqueId), "object"(uniqueId)] in dogBreeds.map(breed)

  //Get all list of dogs for option dropdown menu
  const [dogBreeds, setDogBreeds] = useState([]);
  useEffect(() => {
    fetch("https://dog.ceo/api/breeds/list/all")
      .then((response) => response.json())
      .then(getData => {
        const dogNames = Object.keys(getData.message);
        setDogBreeds(dogNames);
      })
  }, []);

  
  // Populate dog once image is selected
  const [dogImage, setDogImage] = useState();
  const fetchDogImage = (BreedName) => {
    fetch(`https://dog.ceo/api/breed/${BreedName}/images/random`)
    .then(response => response.json())
    .then(getData => {
      setDogImage(getData.message);
    })
  }

  useEffect(() => {
    const options = document.getElementById("dropdownmenu");
    if (options != null) {
      options.addEventListener("change", (event) => {
        const BreedName = event.target.value;
        fetchDogImage(BreedName);
      });
    }
      return () => {
        options.removeEventListener("change", (event) => {
          const BreedName = event.target.value
          fetchDogImage(BreedName);
        });
      };
  }, []);

  // Get random images when click button
  const [subBreed, setSubBreed] = useState([]);
  const [buttonClicked, setButtonClicked] = useState(false);
  const fetchSubBreed = async () => {
    const getData = await fetch("https://dog.ceo/api/breeds/image/random");
    const getSubBreed = await getData.json();
    setSubBreed(getSubBreed);
  };

  useEffect(() => {
    fetchSubBreed();
  }, [buttonClicked]);

  const handleButtonChange = (event) => {
    setButtonClicked(!buttonClicked);
  };

  return (
    <div>
      <div className="row">
        <div className="col-6-sm">
          <select id="dropdownmenu">
            <option>Please select a breed</option>
            {dogBreeds.map((breed, uniqueId) => (
              <option key={uniqueId} value={breed}>
                {breed}
              </option>
            ))}
          </select>
        </div>

        <div className="col-6-sm">
          <button onClick={handleButtonChange}>Click Random Image</button>
        </div>

        <div className="col-6-sm">
          <img
            src={subBreed.message}
            alt="random dog"
            style={{ width: "380px", height: "300px", objectFit: "cover" }}
          ></img>
        </div>

        <div className="col-6-sm">
          <div className="dogImages">
            {[dogImage].map((specificImage) => (
              <img
                id="contain-image"
                src={specificImage}
                key={specificImage}
                style={{ width: "380px", height: "300px", objectFit: "cover" }}
                alt="Dog Images"
              ></img>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default DogBreed;
