import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function SelectedBreed() {
  const [dog, setDog] = useState([]);
  const { name } = useParams();

  useEffect(() => {
    const fetchSingleDog = async () => {
      try {
        const res = await fetch(
          `https://api.thedogapi.com/v1/breeds/search?q=${name}`,
          {
            //limit to 21 images populated
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "x-api-key":
                "live_Xv1NIDcHuYJfH9CnI39Kwmzb0AV6tkxTCQSmsLIaP60jsnCvV9X0T8NZc5Zbl1ak",
            },
          }
        );
        const data = await res.json();
        console.log(data);
        setDog(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchSingleDog();
  }, [name]); //execute everytime name changes in the url

  return (
    <div>
      <div className="container flex items-center justify-center h-screen" id="container">
        {dog.map((breed) => (
          <div key={breed.id} className="text-green-400 grid grid-cols-1 gap-8 p-8 md">
            <img
              src={`https://cdn2.thedogapi.com/images/${breed.reference_image_id}.jpg`}
              alt={breed.name}
            ></img>
            <article>
              <h1>{breed.name}</h1>
              {/* If description exist then we show description if not vice versa */}
              {breed.description && <p>Description: {breed.description}</p>}
              <ul>
                <li>Bred For: {breed.bred_for}</li>
                <li>Height: {breed.height.metric} cm</li>
                <li>Weight: {breed.weight.metric} kgs</li>
                <li>Breed Group: {breed.breed_group}</li>
                <li>Life Span: {breed.life_span}</li>
                {breed.history && <li>History: {breed.history}</li>}
                <li>Temperament: {breed.temperament}</li>
              </ul>
            </article>
          </div>
        ))}
      </div>
    </div>
  );
}
export default SelectedBreed;
