import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function SelectedBreed() {
  const [dog, setDog] = useState([]);
  const { name } = useParams();

  useEffect(() => {
    const fetchSingleDog = async () => {
      try {
        const res = await fetch(
          `https://api.thedogapi.com/v1/breeds/search?q=${name}`,
          {
            //limit to 21 images populated
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "x-api-key":
                "live_Xv1NIDcHuYJfH9CnI39Kwmzb0AV6tkxTCQSmsLIaP60jsnCvV9X0T8NZc5Zbl1ak",
            },
          }
        );
        const data = await res.json();
        setDog(data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchSingleDog();
  }, [name]); //execute everytime name changes in the url

  return (
    <div>
      <div className="container" id="container">
       {dog.map((breed) => {
        <div className="text-green-400" key={breed.id}>
            <img src={`https://cdn2.thedogapi.com/images/${breed.reference_image_id}.jpg`} alt={breed.name}></img>
            <article>
                <h1>{breed.name}</h1>
                <ul>
                    <li>{breed.description}</li>
                    <li>{breed.bred_for}</li>
                    <li>{breed.breed_group}</li>
                    <li>{breed.life_span}</li>
                    <li>{breed.history}</li>
                    <li>{breed.temperament}</li>
                </ul>
            </article>

        </div>
       })}
      </div>
    </div>
  );
}
export default SelectedBreed;
